import React, {useEffect, useState} from "react";
import axios from "axios";

const BudgetHistory = () => {
    const [stockName, setStockName] = useState("씨젠");
    const [transactionDate, setTransactionDate] = useState(
        "2020.08.21");
    const [transactionType, setTransactionType] = useState("매도");
    const [profitLoss, setProfitLoss] = useState("44,290 원")
    const [totalAsset, setTotalAsset] = useState("83,589,400 원")
    const [transactionDetail, setTransactionDetail] = useEffect(() => {
        axios.get(`http://localhost:8080/`)
            .then(response => {
                console.log(`BudgetHistory useEffect then`)
                setStockName(response.data.stockName)
                setTransactionDate(response.data.transactionDate)
                setTransactionType(response.data.transactionType)
                setProfitLoss(response.data.profitLoss)
                setTotalAsset(response.data.totalAsset)
            })
            .catch(error => {
                console.log(`BudgetHistory useEffect err`)
            })
    }, [])


    const linkToDetail = e => {
        e.preventDefault();
    }



    return <>
        <table className="table">
            <thead>
            <tr>
                <th>거래날짜</th>
                <th>종목</th>
                <th>거래 종류</th>
                <th>금액</th>
                <th>잔액</th>
            </tr>
            </thead>
            <tbody>
            {transactionDetail.map((item)=>(
                <tr onClick={linkToDetail}>
                    <td>{item.transactionDate}</td>
                    <td>{item.stockName}</td>
                    <td>{item.transactionType}</td>
                    <td>{item.profitLoss}</td>
                    <td>{item.totalAsset}</td>
                </tr>
            ))}
            </tbody>
        </table>
    </>
}

export default BudgetHistory;
