const BudgetHistory = () => {
    const [stockName, setStockName] = useState("씨젠");
    const [transactionDate, setTransactionDate] = useState(
        "2020.08.21");
    const [transactionType, setTransactionType] = useState("매");
    const [profitLoss, setProfitLoss] = useState("44,290 원")
    const [totalAsset, setTotalAsset] = useState("83,589,400 원")
    const [arr, setArr] = useState([
        {
            stockName : stockName,
            price: price,
            change: change,
            changePercentage: changePercentage,
            marketCap: marketCap,
            volumeCurrency: volumeCurrency
