            marketCap: marketCap,
            volumeCurrency: volumeCurrency
        },{
            stockName : stockName,
            price: price,
            change: change,
            changePercentage: changePercentage,
            marketCap: marketCap,
            volumeCurrency: volumeCurrency
        },{
            stockName : stockName,
            price: price,
            change: change,
            changePercentage: changePercentage,
            marketCap: marketCap,
            volumeCurrency: volumeCurrency
        },{
            stockName : stockName,
            price: price,
            change: change,
            changePercentage: changePercentage,
            marketCap: marketCap,
            volumeCurrency: volumeCurrency
        },{
            stockName : stockName,
            price: price,
            change: change,
            changePercentage: changePercentage,
            marketCap: marketCap,
            volumeCurrency: volumeCurrency
        }
    ])

    const linktoDetail = e => {
        e.preventDefault();
    }

    return <>
        <table className="table">
            <thead>
            <tr>
                <th>거래날짜</th>
                <th>종목</th>
                <th>거래 종류</th>
                <th>금액</th>
                <th>잔액</th>
            </tr>
            </thead>
            <tbody>
            {arr.map((item)=>(
                <tr onClick={linktoDetail}>
                    <td>{item.transactionDate}</td>
                    <td>{item.stockName}</td>
                    <td>{item.transactionType}</td>
                    <td>{item.profitLoss}</td>
                    <td>{item.totalAsset}</td>
                </tr>
            ))}
            </tbody>
        </table>
    </>
}

export default BudgetHistory;
